mapper.select:
select u.id, u.mobile_phone as mobilePhone, u.user_name as userName, u.gender as gender, o.org_name as orgName, u.pwd_valid_date as pwdValidDate, u.last_login_date as lastLoginDate, u.disabled, u.misc_desc as miscDesc from sys_user u left join sys_org_info o on (u.org_id = o.org_id)
u.status = 1
and u.user_name = #{userName}
and u.mobile_phone = #{mobilePhone}
and u.org_id = #{orgId}
